import { Component, OnInit } from '@angular/core';
import { User } from 'src/app/user/user.models';
import { AdminService } from '../admin.service';
import { Registration } from '../registration.models';

@Component({
  selector: 'app-registrations',
  templateUrl: './registrations.component.html',
  styleUrls: ['./registrations.component.css']
})

export class RegistrationsComponent implements OnInit {
  registrations: Registration[] = [];

  constructor(private adminService: AdminService) {}

  ngOnInit(): void {
    this.fetchRegistrations();
  }

  fetchRegistrations(): void {
    this.adminService.getRegistrations().subscribe(
      (data) => this.registrations = data,
      (error) => console.error('Error fetching registrations', error)
    );
  }

  updateStatus(id: string, status: string): void {
    this.adminService.updateRegistrationStatus(id, status).subscribe(
      () => {
        this.fetchRegistrations(); // Refresh after update
      },
      (error) => console.error('Error updating status', error)
    );
  }
}
